import { useState } from "react";
import { addHabit } from "../services/habitService";

export default function HabitForm({ onHabitAdded }: { onHabitAdded: () => void }) {
  const [name, setName] = useState("");

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!name.trim()) return;
    await addHabit({ name, completed: false });
    setName("");
    onHabitAdded(); // Refresh the list in parent
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        maxWidth: "600px",
        margin: "20px auto",
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        gap: "10px",
      }}
    >
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Enter a new habit"
        style={{
          flex: 1,
          padding: "10px",
          borderRadius: "10px",
          border: "1px solid #ccc",
          fontSize: "16px",
        }}
      />
      <button
        type="submit"
        style={{
          padding: "10px 20px",
          borderRadius: "10px",
          border: "none",
          backgroundColor: "#4CAF50",
          color: "white",
          fontWeight: "bold",
          cursor: "pointer",
          fontSize: "16px",
        }}
      >
        Add
      </button>
    </form>
  );
}
