// Replace <your-codespace-name> with the actual Codespace name you see in the browser
const API = "https://<your-codespace-name>-8080.app.github.dev/api/habits";

// replace with real backend URL if different

export const getHabits = async () =>
  fetch(API).then(res => res.json());

export const addHabit = async (habit: any) =>
  fetch(API, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(habit),
  });

export const deleteHabit = async (id: number) =>
  fetch(`${API}/${id}`, { method: "DELETE" });
