import { useEffect, useState } from "react";
import { getHabits, deleteHabit, updateHabit, completeHabit } from "../services/habitService";
import HabitForm from "./HabitForm";

export default function HabitList() {
  const [habits, setHabits] = useState<any[]>([]);

  const refresh = () => getHabits().then(setHabits);

  useEffect(() => {
    refresh();
  }, []);

  const handleDelete = async (id: number) => {
    await deleteHabit(id);
    refresh();
  };

  const handleComplete = async (id: number) => {
    await completeHabit(id);
    refresh();
  };

  const handleEdit = async (habit: any) => {
    const newName = prompt("Edit habit name:", habit.name);
    if (newName && newName.trim() !== "") {
      await updateHabit(habit.id, { ...habit, name: newName });
      refresh();
    }
  };

  return (
    <div>
      {/* Add Habit Form */}
      <HabitForm refresh={refresh} />

      {/* Habit List */}
      <ul>
        {habits.map((h) => (
          <li key={h.id}>
            {h.name} {h.completed ? "✅" : "❌"}
            <button onClick={() => handleDelete(h.id)}>Delete</button>
            <button onClick={() => handleComplete(h.id)}>Complete</button>
            <button onClick={() => handleEdit(h)}>Edit</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
